package shapes.project;

public class TriangularPrism {
    private double base;
    private double height;
    private double length;

    public TriangularPrism(double base, double height, double length) {
        this.base = base;
        this.height = height;
        this.length = length;
    }

    public double getVolume() {
        return 0.5 * base * height * length;
    }

    public void print() {
        System.out.printf("Base: %.2f, Height: %.2f, Length: %.2f, Volume: %.2f%n",
                base, height, length, getVolume());
    }

    @Override
    public String toString() {
        return String.format("Base: %.2f, Height: %.2f, Length: %.2f, Volume: %.2f",
                base, height, length, getVolume());
    }
}
