package shapes.project;

import java.io.*;

public class WriteReadTriangularPrisms {
    public static void main(String[] args) {
        String filename = "TriangularPrisms6.txt";

        TriangularPrism[] prisms = {
                new TriangularPrism(3, 4, 5),
                new TriangularPrism(5, 6, 7),
                new TriangularPrism(2, 3, 4),
                new TriangularPrism(6, 8, 10),
                new TriangularPrism(4, 4, 4),
                new TriangularPrism(7, 3, 9)
        };

        try (PrintWriter writer = new PrintWriter(new FileWriter(filename))) {
            for (TriangularPrism prism : prisms) {
                writer.println(prism);
            }
            System.out.println("âœ… 6 triangular prisms written to file: " + filename);
        } catch (IOException e) {
            System.out.println("Error writing file: " + e.getMessage());
        }

        System.out.println("\nðŸ“– Reading file content:");
        try (BufferedReader reader = new BufferedReader(new FileReader(filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                System.out.println(line);
            }
        } catch (IOException e) {
            System.out.println("Error reading file: " + e.getMessage());
        }
    }
}
